package day02;

public class VarDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age;
		
		age = -9;
		System.out.println(age);
		System.out.println(Integer.toBinaryString(age));
		
		age = 16;

		System.out.println(age);
		System.out.println(Integer.toBinaryString(age));
		
		
		
		
		int age2 = 17;
		System.out.println(age + age2);
	}

}
